mapper.insert:
INSERT INTO CATEGORY (	  CATEGORY_ID
						, MAIN_CATEGORY
						, MIDDLE_CATEGORY
						, SMALL_CATEGORY )
VALUES (  #{categoryId}
		, #{mainCategory}
		, #{middleCategory}
		, #{smallCategory} )

mapper.update:
UPDATE CATEGORY SET MAIN_CATEGORY   = #{mainCategory}
				  , MIDDLE_CATEGORY = #{middleCategory}
				  , SMALL_CATEGORY  = #{smallCategory}
WHERE CATEGORY_ID = #{categoryId}
