mapper.insert:
INSERT INTO CONSULTING(
	CONSULTING_ID
	,CONSULTING_CONTENTS
	,CONSULTING_DESIRED_DATE
	,CONSULTING_STATE
	,CONSULTING_TITLE
	
		,COMPANY_ID
	
	)
VALUES (
	CONSULTING_ID_SEQ.NEXTVAL
	,#{consultingContents}
	,TO_DATE(REPLACE(#{consultingDesiredDate}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	,'C1'
	, #{consultingTitle}
	
		,#{companyId}
	
)

mapper.update:
UPDATE CONSULTING

	CONSULTING_DESIRED_DATE = TO_DATE(REPLACE(#{consultingDesiredDate}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	,CONSULTING_CONTENTS 	= #{consultingContents}
	,CONSULTING_TITLE 		= #{consultingTitle}
	
	
		,CONSULTING_DATE = TO_DATE(REPLACE(#{consultingDate}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	
	
		,CONSULTING_REMARKS = #{consultingRemarks}
	
	
		,COMPANY_ID = #{companyId}
	
	 
		,CONSULTING_STATE = #{consultingState}
	


	CONSULTING_ID = #{consultingId}

mapper.select:
SELECT B.* FROM( SELECT A.*, ROWNUM RN FROM ( SELECT CONSULTING_ID ,COMPANY_ID ,CONSULTING_CONTENTS ,REPLACE(TO_CHAR(CONSULTING_DESIRED_DATE, 'YYYY-MM-DD HH24:MI'), ' ', 'T') "consultingDesiredDate" ,REPLACE(TO_CHAR(CONSULTING_DATE, 'YYYY-MM-DD HH24:MI'), ' ', 'T') "consultingDate" ,CONSULTING_REMARKS ,CONSULTING_STATE ,CONSULTING_TITLE FROM CONSULTING
COMPANY_ID = #{companyId}
ORDER BY CONSULTING_ID ) A ) B
RN BETWEEN #{start} AND #{end}

SELECT B.* FROM (SELECT A.* , ROWNUM RN FROM ( SELECT CONSULTING_ID ,COMPANY_ID ,CONSULTING_TITLE ,CONSULTING_DESIRED_DATE "consultingDesiredDate" ,CONSULTING_STATE ,FIND_CODENM(CONSULTING_STATE) AS consultingStateName FROM CONSULTING
${searchConsulting} like '%' || #{searchKeyword} || '%'
ORDER BY CONSULTING_ID DESC
ORDER BY ${sort}
) A ) B WHERE RN >=#{start} AND RN <=#{end}

mapper.update:
UPDATE CONSULTING

	CONSULTING_CONTENTS 	= #{consultingContents}
	,CONSULTING_TITLE 		= #{consultingTitle}
	,CONSULTING_STATE		= #{consultingState}
	,CONSULTING_DESIRED_DATE = TO_DATE(REPLACE(#{consultingDesiredDate}, 'T', ' '), 'YYYY-MM-DD HH24:MI')	
	
		,CONSULTING_DATE = TO_DATE(REPLACE(#{consultingDate}, 'T', ' '), 'YYYY-MM-DD HH24:MI')
	  
	
		,CONSULTING_REMARKS = #{consultingRemarks}
	

WHERE CONSULTING_ID = #{consultingId}

