mapper.insert:
INSERT INTO ESTIMATE (	  ESTIMATE_ID
						, ESTIMATE_PRICE
						, REQUEST_DAY
						, TRADE_PROGRESS
						, REMARKS
						, APPLICANTS

						, LEVEL_OF_DIFFICULTY
						, CANDIDATE
						, APPLY_PURPOSE
						, EXAM_DATE
						, CATEGORY_ID

						, EXAM_CLASSFICATION
						, COMPANY_ID
						, EXAM_COUNT
						, EXAM_INTERVAL
						, ESTIMATE_NAME )
VALUES ( #{estimateId}
		,#{estimatePrice}
		,SYSDATE 
		,#{tradeProgress}
		,#{remarks}
		,#{applicants}

		,#{levelOfDifficulty}
		,#{candidate}
		,#{applyPurpose}
		,#{examDate}
		,#{categoryId}

		,#{examClassfication}
		,#{companyId}
		,#{examCount}
		,#{examInterval}
		,#{estimateName})

mapper.update:
UPDATE ESTIMATE
SET	  ESTIMATE_ID 		= #{estimateId}
	, ESTIMATE_PRICE 	= #{estimatePrice}
	, REQUEST_DAY 		= SYSDATE
	, TRADE_PROGRESS 	= #{tradeProgress}
	, REMARKS 			= #{remarks}
	, APPLICANTS		= #{applicants}

	, LEVEL_OF_DIFFICULTY = #{levelOfDifficulty}
	, CANDIDATE 		= #{candidate}
	, APPLY_PURPOSE 	= #{applyPurpose}
	, EXAM_DATE 		= #{examDate}
	, CATEGORY_ID		= #{categoryId}

	, EXAM_CLASSFICATION= #{examClassfication}
	, COMPANY_ID		= #{companyId}
	, EXAM_COUNT		= #{examCount}
	, EXAM_INTERVAL		= #{examInterval}
	, ESTIMATE_NAME		= #{estimateName}

WHERE ESTIMATE_ID = #{estimateId}
