mapper.insert:

	SELECT MAX(ESTIMATE_ID)+1 FROM ESTIMATE

INSERT INTO ESTIMATE (	  ESTIMATE_ID
						, ESTIMATE_PRICE
						, REQUEST_DAY
						, TRADE_PROGRESS
						, REMARKS
						, APPLICANTS

						, LEVEL_OF_DIFFICULTY
						, CANDIDATE
						, APPLY_PURPOSE
						, EXAM_DATE
						, CATEGORY_ID

						, EXAM_CLASSFICATION
						, COMPANY_ID
						, EXAM_INTERVAL
						, ESTIMATE_NAME )
VALUES ( #{estimateId}
		,#{estimatePrice}
		,SYSDATE 
		, 'B1' 
		,#{remarks}
		,#{applicants}

		,#{levelOfDifficulty}
		,#{candidate}
		,#{applyPurpose}
		,#{examDate}
		,#{categoryId}

		,#{examClassfication}
		,#{companyId}
		,#{examInterval}
		,#{estimateName})

mapper.update:
UPDATE ESTIMATE
SET	  ESTIMATE_ID 		= #{estimateId}
	, ESTIMATE_PRICE 	= #{estimatePrice}
	, REQUEST_DAY 		= SYSDATE
	, TRADE_PROGRESS 	= #{tradeProgress}
	, REMARKS 			= #{remarks}
	, APPLICANTS		= #{applicants}

	, LEVEL_OF_DIFFICULTY = #{levelOfDifficulty}
	, CANDIDATE 		= #{candidate}
	, APPLY_PURPOSE 	= #{applyPurpose}
	, EXAM_DATE 		= #{examDate}
	, CATEGORY_ID		= #{categoryId}

	, EXAM_CLASSFICATION= #{examClassfication}
	, COMPANY_ID		= #{companyId}
	, EXAM_INTERVAL		= #{examInterval}
	, ESTIMATE_NAME		= #{estimateName}

WHERE ESTIMATE_ID = #{estimateId}

mapper.select:
SELECT B.* FROM( SELECT A.*, ROWNUM RN FROM ( SELECT E.ESTIMATE_ID "estimateId" , C.CATEGORY_NAME "categoryName" , E.ESTIMATE_PRICE "estimatePrice" , E.REQUEST_DAY "requestDay" , E.TRADE_PROGRESS "tradeProgress" , FIND_CODENM(TRADE_PROGRESS) "tradeProgressName" , E.REMARKS , E.APPLICANTS , E.LEVEL_OF_DIFFICULTY , FIND_CODENM(LEVEL_OF_DIFFICULTY) "levelOfDifficultyName" , E.CANDIDATE , FIND_CODENM(CANDIDATE) "candidateName" , E.APPLY_PURPOSE , FIND_CODENM(APPLY_PURPOSE) "applyPurposeName" , E.EXAM_DATE , E.CATEGORY_ID , E.EXAM_CLASSFICATION , FIND_CODENM(EXAM_CLASSFICATION) "examClassficationName" , E.COMPANY_ID , E.EXAM_COUNT , FIND_CODENM(EXAM_COUNT) "examCountName" , E.EXAM_INTERVAL , FIND_CODENM(EXAM_INTERVAL) "examIntervalName" , E.ESTIMATE_NAME
FROM ESTIMATE E JOIN CATEGORY C ON C.CATEGORY_ID = E.CATEGORY_ID
COMPANY_ID = #{companyId} ESTIMATE_ID = #{estimateId}
ORDER BY E.ESTIMATE_ID DESC ) A ) B
RN BETWEEN #{start} AND #{end}
