mapper.insert:
INSERT INTO EXAM (	
					EXAM_ID
				,	EXAM_START_TIME
				,	EXAM_END_TIME
				,	QUESTION_QUANTITY
				,	ESTIMATE_ID
				,	NUMBER_OF_TIMES
				,	PASSING_SCORE
				,	POSSIBLE_APPLICANTS
				,	EXAM_NAME
				,	EXAM_STATUS
				,	DISCLOSURE_STATUS
				,	SET_EXAM_STATUS
				,	EXAM_DESCRIPTION_SIMPLE
				,	EXAM_DESCRIPTION_DETAIL
			)
VALUES ( 
			EXAM_ID_SEQ.NEXTVAL
		,	TO_DATE(#{examStartTime}, 'YYYY-MM-DD HH24:MI:SS')
		,	TO_DATE(#{examEndTime}, 'YYYY-MM-DD HH24:MI:SS')
		,	#{questionQuantity}
		,	#{estimateId}
		,	#{numberOfTimes}
		,	#{passingScore}
		,	#{possibleApplicants}
		,	#{examName}
		,	#{examStatus} 
		,	#{disclosureStatus} 
		,	#{setExamStatus} 
		,	#{examDescriptionSimple} 
		,	#{examDescriptionDetail} 
	)

mapper.update:
UPDATE EXAM

	EXAM_START_TIME   			= TO_DATE(#{examStartTime}, 'YYYY-MM-DD HH24:MI:SS')
	, EXAM_END_TIME				= TO_DATE(#{examEndTime}, 'YYYY-MM-DD HH24:MI:SS')
	, QUESTION_QUANTITY 		= #{questionQuantity}
	, ESTIMATE_ID  				= #{estimateId}
	, NUMBER_OF_TIMES 			= #{numberOfTimes}
	, PASSING_SCORE 			= #{passingScore}
	, POSSIBLE_APPLICANTS 		= #{possibleApplicants}
	, EXAM_NAME 				= #{examName}
	, EXAM_STATUS 				= #{examStatus}
	, DISCLOSURE_STATUS 		= #{disclosureStatus}
	, SET_EXAM_STATUS 			= #{setExamStatus}
	, EXAM_DESCRIPTION_SIMPLE 	= #{examDescriptionSimple}
	, EXAM_DESCRIPTION_DETAIL 	= #{examDescriptionDetail}


	EXAM_ID = #{examId}

mapper.select:
SELECT EXAM_ID , TO_CHAR(EXAM_START_TIME, 'YYYY-MM-DD HH24:MI') "examStartTime" , TO_CHAR(EXAM_END_TIME, 'YYYY-MM-DD HH24:MI') "examEndTime" , QUESTION_QUANTITY , ESTIMATE_ID , NUMBER_OF_TIMES , PASSING_SCORE , POSSIBLE_APPLICANTS , EXAM_NAME , EXAM_STATUS , DISCLOSURE_STATUS , SET_EXAM_STATUS , EXAM_DESCRIPTION_SIMPLE , EXAM_DESCRIPTION_DETAIL FROM EXAM
EXAM_ID = #{examId}
ORDER BY EXAM_ID
