mapper.insert:
INSERT INTO EXAM (	
					EXAM_ID
				,	EXAM_START_TIME
				,	EXAM_END_TIME
				,	QUESTION_QUANTITY
				,	ESTIMATE_ID
				,	NUMBER_OF_TIMES
				,	PASSING_SCORE
				,	POSSIBLE_APPLICANTS
				,	EXAM_NAME
				,	EXAM_STATUS
				,	DISCLOSURE_STATUS
				,	SET_EXAM_STATUS
			)
VALUES ( 
			#{examId}
		,	#{examStartTime}
		,	#{examEndTime}
		,	#{questionQuantity}
		,	#{estimateId}
		,	#{numberOfTimes}
		,	#{passingScore}
		,	#{possibleApplicants}
		,	#{examName}
		,	#{examStatus} 
		,	#{disclosureStatus} 
		,	#{setExamStatus} 
	)

mapper.update:
UPDATE EXAM

	EXAM_START_TIME   		= #{examStartTime}
	, EXAM_END_TIME			= #{examEndTime}
	, QUESTION_QUANTITY 	= #{questionQuantity}
	, ESTIMATE_ID  			= #{estimateId}
	, NUMBER_OF_TIMES 		= #{numberOfTimes}
	, PASSING_SCORE 		= #{passingScore}
	, POSSIBLE_APPLICANTS 	= #{possibleApplicants}
	, EXAM_NAME 			= #{examName}
	, EXAM_STATUS 			= #{examStatus}
	, DISCLOSURE_STATUS 	= #{disclosureStatus}
	, SET_EXAM_STATUS 		= #{setExamStatus}


	EXAM_ID = #{examId}
